function show(c){basicFunc(counter+=c)}function showDot(c){basicFunc(counter=c)}function basicFunc(){counter==cars.length&&(counter=0),counter<0&&(counter=cars.length-1);for(var c=0;c<cars.length;c++)cars[c].style.display="none",cars[counter].style.display="block";for(var o=0;o<dots.length;o++)dots[o].className="dot";dots[counter].className+=" act"}window.onload=basicFunc;var cars=document.querySelectorAll(".slide"),dots=document.querySelectorAll(".dot"),counter=0;